import React from "react";
import { useRouter } from "next/router";
// import { Link } from "next/link";
import Iamport from "react-iamport";

const Index = () => {
  const router = useRouter();

  const handleSuccess = (res) => {
    console.group("handleSuccess");
    console.log(res);
    console.groupEnd("handleSuccess");
  };

  const handleFailed = (err) => {
    console.group("handleFailed");
    console.log(err);
    console.groupEnd("handleFailed");
  };

  return (
    <div style={styles.container}>
      <span style={styles.title}>iamport 결제관련한 sample web입니다.</span>
      {/* <Link href={"/payment"}> */}
      {/* <a onClick={handleClick}>결제화면</a> */}
      {/* </Link> */}
      <Iamport
        params={{
          pg: "inicis",
          pay_method: "card",
          merchant_uid: "merchant_" + new Date().getTime(),
          name: "주문명:결제테스트",
          amount: 222,
          buyer_email: "buyer@email.com",
          buyer_name: "정지승",
          buyer_tel: "010-1234-5678",
          buyer_addr: "서울특별시 강남구 삼성동",
          buyer_postcode: "123-456",
          m_redirect_url: "https://www.yourdomain.com/payments/complete",
        }}
        jqueryLoaded={false}
        onFailed={handleFailed}
        onSuccess={handleSuccess}
        render={(renderProps) => (
          <button style={styles.pay_btn}>결제하기</button>
        )}
      />
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    flex: 1,
    flexDirection: "column",
    alignItems: "center",
  },
  title: {
    flex: 1,
    margin: "20",
  },
  pay_btn: {
    width: 300,
    border: 0,
  },
};
export default Index;
