import React, { useState, useEffect } from "react";
import { useRouter } from "next/router";
// import { Link } from "next/link";
import Iamport from "react-iamport";
import axios from "axios";
import Head from "next/head";

const Index = () => {
  const [data, setData] = useState({});
  const router = useRouter();

  const handlePaments = (e) => {
    // method, merchant_uid, name, amount, buyer: {email, name, tell, name, tel, addr, postcode}

    // 4주 인텐시브
    /*
    인앱상품 정보
    1. 기본 구성 인앱 상품 템플릿
      = 수강권, 준비물

      a. 클래스 기본 구성
        > 할인가격, 상품명

    2. 추가 준비물 선택
      = 상품 이미지, 상품명, 수량, 가격
    */

    /*
    배송 정보
    이름, 휴대폰번호, 우편주소, 주소, 도로명주소, 상세주소
    배송 요청사항
    */

    /* 신청 내역 확인 및 결제 - OFFLine_Inten_PAY-STEP3-OFF-001
    결제정보
    일반결제 / 간편 카드등록(정기결제)
    배송비
    */

    try {
      IMP.request_pay(
        {
          pg: "nice",
          pay_method: "card",
          merchant_uid: "merchant_" + new Date().getTime(),
          name: "주문명:결제테스트",
          amount: 14000,
          buyer_email: "iamport@siot.do",
          buyer_name: "구매자이름",
          buyer_tel: "010-1234-5678",
          buyer_addr: "서울특별시 강남구 삼성동",
          buyer_postcode: "123-456",
          m_redirect_url: "https://www.yourdomain.com/payments/complete",
        },
        (res) => {
          if (res.success) {
            console.log("rsp ", rsp);
          } else {
            console.log("err ", res.error_msg);
          }
        }
      );
    } catch (e) {
      console.log("error ", e);
    }
  };

  useEffect(() => {
    if (window.IMP) {
      const { IMP } = window;
      const userCode = "imp94304194";
      IMP.init(userCode);
    }
  }, []);

  return (
    <div style={styles.container}>
      <Head>
        <script
          type="text/javascript"
          src="https://code.jquery.com/jquery-1.12.4.min.js"
        ></script>
        <script
          type="text/javascript"
          src="https://cdn.iamport.kr/js/iamport.payment-1.1.5.js"
        ></script>
      </Head>
      <span style={styles.title}>iamport Nicepay Payments</span>
      <button style={styles.pay_btn} onClick={handlePaments}>
        paments
      </button>
      <div style={styles.card_wrap}></div>
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    flex: 1,
    flexDirection: "column",
    alignItems: "center",
  },
  title: {
    flex: 1,
    margin: 20,
  },
  pay_btn: {
    width: 300,
    margin: 10,
    border: 0,
  },
  card_wrap: {
    padding: "100 50",
    backgroundColor: "red",
  },
};
export default Index;
